import React, { useState, useReducer } from "react";
import CreatePost from "./CreatePost";
import UserBar from "./Userbar";
import PostList from "./PostList";
import "./App.css";

function App(){

const initialPosts= [
  {title: "Node JS", description: "Used for backend", author:"Vishal"},
  {title: "React", description: "Used for frontend", author:"Vishal"}
]



const [createPostVisible, setCreatePostVisible] = useState(false);

  function userReducer(state, action) {
    switch (action.type) {
      case "LOGIN":
        setCreatePostVisible(true);
        return action.username;
      case "REGISTER":
        setCreatePostVisible(true);
        return action.username;
      case "LOGOUT":
        setCreatePostVisible(false);
        return null;
      default:
        return state;
    }
  }

  const [user, dispatchUser] = useReducer(userReducer, "");

  function postReducer(state, action) {
    switch (action.type) {
      case "CREATE_POST":
        const newPost = {
          title: action.post.title,
          content: action.post.content,
          author: user,
          complete: false,
          dateCreated: new Date().toISOString(),
          dateCompleted: null,
        };
        return [newPost, ...state];

      case "TOGGLE_POST":
        const updatedPosts = state.map((post) => {
          if (post.title === action.title) {
            post.complete = !post.complete;
            post.dateCompleted = post.complete ? new Date().toISOString() : null;
          }
          return post;
        });
        return updatedPosts;

      case "DELETE_POST":
        return state.filter((post) => post.title !== action.title);

      default:
        return state;
    }
  }

  const [posts, dispatchPost] = useReducer(postReducer, initialPosts);

  const handleAddPost = (newPost) => {
    console.log(newPost);
    dispatchPost({ type: "CREATE_POST", ...newPost });
  };

  const handleDeletePost = (title) => {
    dispatchPost({ type: "DELETE_POST", title });
  };


  return (
    <div className="app-container">
      <UserBar user={user} setUser={setUser} />
      {user && (
        <CreatePost
          user={user}
          handleAddPost={handleAddPost}
          setUser={setUser} 
        />
      )}
      <PostList posts={posts} handleDeletePost={handleDeletePost} />
    </div>
  );
}

export default App;
