import { useState } from "react"

export default function CreateTodo ({user, handleAddPost}) {


    const [ title, setTitle ] = useState('')
const [ content, setContent ] = useState('')


    function handleTitle (evt) { setTitle(evt.target.value); }
    function handleContent (evt) { setContent(evt.target.value) }
    function handleCreate () {
    const newPost = { title, content, author: user };
    handleAddPost(newPost);
    }

    return (
    <form onSubmit={(e) => {e.preventDefault(); handleCreate();}}>
    <div>Author: <b>{user}</b></div>
    <div>
    <label htmlFor="create-title">Title:</label>
    <input type="text" value={title} onChange={handleTitle}  name="create-title" id="create-title" />
    </div>
    <div>
    <label htmlFor="create-title">Description:</label>
    <textarea value={content} onChange={handleContent} name="create-content" id="create-content" />
    </div>   
    <input type="submit" value="Create" />
    </form>
    )
    }
