import { useState } from "react";
import CreatePost from "./CreatePost";
import Login 
 from "./Login";
import Logout from "./Logout";
import Register from "./Register";
import UserBar from "./Userbar";
import PostList from "./Postlist";
function App() {
  const [user,setUser] = useState("");

  const initialPosts =[
     {title: "React Hooks", content:"The greatest thing is a slice of bread", author:"Vishal"},
     {title: "using react fragments", content:"Keep the dorm tree clean", author:"Vishal"},
     {title: "Component Resusability", content:"Make your own comments", author:"Vishal"},
  ];

const [posts, setPosts] = useState(initialPosts)

const handleAddPost = (newPost) => {
        setPosts([newPost, ...posts]);
}

  return (
    <div>
    <Login />
    <Logout />
    <Register />
    <UserBar user={user} setUser={setUser}/>
    <CreatePost user={user} handleAddPost={handleAddPost}/>
    <PostList posts={posts} />
    </div>
  );
}

export default App;
